import React from "react";
import "./lesson.css";

import { useState } from "react";

import { data } from "../../helper/data";

const LessonCard = () => {

    const [data, setData] = useState({...data});


    const handleClear = () => {

      setData(null);
      
    };




  return (
    <div className="container">
      <h1 className="title-lesson">6 Lesson Today</h1>
      <div className="lesson-card">
       
        {data.map((lesson) => {
          return (
            <div className="lessons">
              <div className="lesson-img">
                <img src={lesson.image} alt="" srcset="" />
              </div>{" "}
              <div className="lesson-info">
                <p className="lesson-name">
                  Lesson Name: <span>{lesson.name}</span>
                </p>
                <p className="lesson-hour">
                  Lesson Hour:<span>{lesson.hour}</span>
                </p>
              </div>
            </div>
          );
        })}
      </div>

      <button type="button" className="clear-btn" onClick={handleClear}>
        Clear List
      </button>
    </div>
  );
};

export default LessonCard;
