import React from 'react'
import './lesson.css'

import { data } from '../../helper/data'


const LessonCard = () => {
  return (
    

    <div className='lesson-card'>
    {data.map((lesson)=> {
        return <div><img src={lesson.image} alt="" srcset="" /></div>
    })}
    
    </div>


   
    
   
)
}

export default LessonCard
