import React from 'react'
import './lesson.css'

import { data } from '../../helper/data'


const LessonCard = () => {
  return (
    

    <div className='container'   >
    <h1 className='title-lesson'>6 Lesson Today</h1>
    {data.map((lesson)=> {
        return <div className='lesson-card'><img src={lesson.image} alt="" srcset="" /> <div><p className='lesson-name'>Lesson Name:  <span>{lesson.name}</span></p><p className='lesson-hour'>Lesson Hour:<span>{lesson.hour}</span></p></div></div>
    })}

    <button type="button" className='clear-btn'>Clear List</button>
    
    </div>


   
    
   
)
}

export default LessonCard
