import React, { useState } from "react";
import "./lesson.css";
import { data } from "../../helper/data";

const LessonCard = () => {
  // Ders listesi için bir state ve bu state'i güncellemek için bir fonksiyon tanımlayın
  const [lessonList, setLessonList] = useState(data);

  // "Clear List" butonuna basıldığında ders listesini boş bir diziyle güncelleyen fonksiyon
  const handleClear = () => {
    setLessonList([]); // Ders listesini boş bir diziyle güncelle
  };

  return (
    <div className="container">
      <h1 className="title-lesson">Bugün 6 Ders Var</h1>
      <div className="lesson-card">
        {/* lessonList'i haritalayarak ders kartlarını oluşturun */}
        {lessonList.map((lesson) => (
          <div className="lessons" key={lesson.id}>
            <div className="lesson-img">
              <img src={lesson.image} alt="" />
            </div>
            <div className="lesson-info">
              <p className="lesson-name">
                Ders Adı: <span>{lesson.name}</span>
              </p>
              <p className="lesson-hour">
                Ders Saati:<span>{lesson.hour}</span>
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* "Clear List" butonuna tıklandığında handleClear fonksiyonunu çağırın */}
      <button type="button" className="clear-btn" onClick={handleClear}>
        Listeyi Temizle
      </button>
    </div>
  );
};

export default LessonCard;
