import React from 'react'
import './lesson.css'

import { data } from '../../helper/data'


const LessonCard = () => {
  return (
    <div className='container'>

    <div className='lesson-card'>
    {data.map((item) => {
      return (
        <div className='card' key={item.id}>
          <img src={item.image} alt="lesson" />
          <h3>{item.name}</h3>
          <p>{item.hour}</p>
        </div>
      );
    })}
    
    </div>


   
    
   

          </div> )
}

export default LessonCard
