import React, { useState } from "react"; // Include useState in curly braces
import "./lesson.css";
import lessonData from "../../helper/data"; // Rename the import variable

const LessonCard = () => {
  const [lessonList, setLessonList] = useState(lessonData); // Rename state variable

  const handleClear = () => {
    setLessonList([]); // Clear the lessonList state
  };

  return (
    <div className="container">
      <h1 className="title-lesson">6 Lesson Today</h1>
      <div className="lesson-card">
        {lessonList.map((lesson) => (
          <div className="lessons" key={lesson.id}>
            <div className="lesson-img">
              <img src={lesson.image} alt="" />
            </div>
            <div className="lesson-info">
              <p className="lesson-name">
                Lesson Name: <span>{lesson.name}</span>
              </p>
              <p className="lesson-hour">
                Lesson Hour:<span>{lesson.hour}</span>
              </p>
            </div>
          </div>
        ))}
      </div>
      <button type="button" className="clear-btn" onClick={handleClear}>
        Clear List
      </button>
    </div>
  );
};

export default LessonCard;
